import React, { useState } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import AcademicsPopup from './components/AcademicsPopup';
import FacultyPopup from './components/FacultyPopup';
//import ResearchPopup from './components/ResearchPopup';
//import AdmissionsPopup from './components/AdmissionsPopup';
//import FacilitiesPopup from './components/FacilitiesPopup';
//import StudentsPopup from './components/StudentsPopup';
//import HostelsPopup from './components/HostelsPopup';
//import AboutPopup from './components/HostelsPopup';
// import other necessary components

function CustomNavbar({ stickyClass, navbarOpacity }) {
  const [showPopup, setShowPopup] = useState({
    academics: false,
    faculty: false,
    research: false,
    admissions: false,
    facilities: false,
    studentsLife: false,
    newsAndEvents: false,
    hostels: false,
    about: false
  });

  const togglePopup = (menu, status) => setShowPopup({ ...showPopup, [menu]: status });

  return (
    <Navbar expand="lg" className={`w-100 ${stickyClass || 'position-absolute'}`} style={{ opacity: navbarOpacity }}>
      <Container>
        <Navbar.Toggle aria-controls='basic-navbar-nav' />
        <Navbar.Collapse id='basic-navbar-nav'>
          <Nav className='me-auto justify-content-center w-100'>
            {/* Each nav item with its popup */}
            <div className='nav-item' onMouseEnter={() => togglePopup('academics', true)} onMouseLeave={() => togglePopup('academics', false)}>
              <span className='text-uppercase'>Academics</span>
              {showPopup.academics && <AcademicsPopup />}
            </div>
            {/* Repeat for other nav items */}
            {/* Faculty */}
            <div className='nav-item' onMouseEnter={() => togglePopup('faculties', true)} onMouseLeave={() => togglePopup('faculties', false)}>
              <span className='text-uppercase'>Faculty</span>
              {showPopup.faculty && <FacultyPopup />}
            </div>
            {/* Research */}
            {/* ... and so on for each nav item */}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default CustomNavbar;
