import React, { useState } from 'react';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
import FacultyPopup from './components/FacultyPopup'; // Import FacultyPopup component
import './App.css';

function CustomNavbar() {
  const [showPopup, setShowPopup] = useState({});

  const togglePopup = (popup) => {
    setShowPopup({ ...showPopup, [popup]: !showPopup[popup] });
  };

  return (
    <Navbar expand="lg" bg="light" variant="light">
      <Container>
        <Navbar.Brand href="#home">QUEST | Nawabshah</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            {/* Repeat for other menu items */}
            <div className='nav-item' onMouseEnter={() => togglePopup('faculties')} onMouseLeave={() => togglePopup('faculties')}>
              <span className='text-uppercase'>Faculties</span>
              {showPopup.faculties && <FacultyPopup />}
            </div>
            {/* Add other Nav items and dropdowns here */}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default CustomNavbar;
