import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';


const FacultyProfile = () => {
    const [facultyData, setFacultyData] = useState({
        username: '', 
        name: '', 
        email: '', 
        phoneNumber: '', 
        department: ''
    });
    const { id } = useParams();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`http://localhost:3001/faculty/${id}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setFacultyData(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };

        fetchData();
    }, [id]);

    const handleUpdate = async (field) => {
        try {
            const response = await fetch(`http://localhost:3001/updateFaculty`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ ...facultyData, fieldToUpdate: field }),
            });

            if (!response.ok) throw new Error(await response.text());
            alert(`${field} updated successfully`);

            // Fetch updated data to reflect the changes
            const updatedResponse = await fetch(`http://localhost:3001/faculty/${id}`);
            const updatedData = await updatedResponse.json();
            setFacultyData(updatedData);
        } catch (error) {
            console.error('Update error:', error);
            alert('Update failed: ' + error.message);
        }
    };

    return (
        <div>
            <h1>Faculty Profile</h1>
            <div>
                <label>Username: {facultyData.username}</label>
            </div>
            <div>
                <label>Name:</label>
                <input 
                    type="text" 
                    value={facultyData.name} 
                    onChange={e => setFacultyData({ ...facultyData, name: e.target.value })}
                />
                <button onClick={() => handleUpdate('name')}>Update Name</button>
            </div>
            <div>
                <label>Email:</label>
                <input 
                    type="email" 
                    value={facultyData.email} 
                    onChange={e => setFacultyData({ ...facultyData, email: e.target.value })}
                />
                <button onClick={() => handleUpdate('email')}>Update Email</button>
            </div>
            <div>
                <label>Phone Number:</label>
                <input 
                    type="text" 
                    value={facultyData.phoneNumber} 
                    onChange={e => setFacultyData({ ...facultyData, phoneNumber: e.target.value })}
                />
                <button onClick={() => handleUpdate('phoneNumber')}>Update Phone Number</button>
            </div>
            <div>
                <label>Department:</label>
                <select 
                    value={facultyData.department} 
                    onChange={e => setFacultyData({ ...facultyData, department: e.target.value })}
                >
                    <option value="">Select Department</option>
                    <option value="Computer Systems Engineering">Computer Systems Engineering</option>
                    <option value="Electronics Engineering">Electronics Engineering</option>
                    <option value="Civil Engineering">Civil Engineering</option>
                    {/* Add more options as needed */}
                </select>
                <button onClick={() => handleUpdate('department')}>Update Department</button>
            </div>

            {/* Displaying updated data */}
            <div>
                <h2>Profile</h2>
                <p>Name: {facultyData.name}</p>
                <p>Email: {facultyData.email}</p>
                <p>Phone Number: {facultyData.phoneNumber}</p>
                <p>Department: {facultyData.department}</p>
            </div>
        </div>
    );
};

export default FacultyProfile;
