import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './GenericStyles.css';

const FacultyProfile = () => {
    const [facultyData, setFacultyData] = useState({
        username: '', 
        name: '', 
        email: '', 
        phoneNumber: '', 
        department: '',
        publications: []
    });
    const [showPublicationModal, setShowPublicationModal] = useState(false);
    const [newPublication, setNewPublication] = useState({ type: "", details: "" });
    const { id } = useParams();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`http://localhost:3001/faculty/${id}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setFacultyData({ ...data, publications: data.publications || [] });
            } catch (error) {
                console.error('Error:', error);
            }
        };

        fetchData();
    }, [id]);

    const handleUpdate = async (field, value) => {
        try {
            const updatedData = { ...facultyData, [field]: value };
            const response = await fetch(`http://localhost:3001/updateFaculty`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(updatedData)
            });

            if (!response.ok) throw new Error(await response.text());
            setFacultyData(updatedData);
            alert('Profile updated successfully');
        } catch (error) {
            console.error('Update error:', error);
            alert('Update failed: ' + error.message);
        }
    };

    const addPublication = async () => {
        try {
            const response = await fetch('http://localhost:3001/addPublication', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    facultyId: id, // The faculty ID
                    publicationType: newPublication.type,
                    details: newPublication.details
                })
            });
    
            if (!response.ok) throw new Error(await response.text());
    
            // Add the new publication to the local state to update the UI
            const savedPublication = await response.json();
            setFacultyData({
                ...facultyData,
                publications: [...facultyData.publications, savedPublication]
            });
    
            setNewPublication({ type: "", details: "" });
            setShowPublicationModal(false);
            alert('Publication added successfully');
        } catch (error) {
            console.error('Error adding publication:', error);
            alert('Failed to add publication: ' + error.message);
        }
    };

    return (
        <div className="cs-container">
            <h1>Welcome {facultyData.username}</h1>
            <div className="form">
                {/* Faculty update fields */}
                {/* Each field should have a corresponding input and update button */}
                {/* For simplicity, only showing name and email as examples */}
                <div>
                    <label>Name:</label>
                    <input 
                        type="text" 
                        value={facultyData.name} 
                        onChange={e => setFacultyData({ ...facultyData, name: e.target.value })}
                    />
                    <button onClick={() => handleUpdate('name', facultyData.name)}>Update Name</button>
                </div>
                <div>
                    <label>Email:</label>
                    <input 
                        type="email" 
                        value={facultyData.email} 
                        onChange={e => setFacultyData({ ...facultyData, email: e.target.value })}
                    />
                    <button onClick={() => handleUpdate('email', facultyData.email)}>Update Email</button>
                </div>
                {/* Add other fields like phone number, department, etc. */}
            </div>

            <div className="profile-display">
                <h2>Profile Information</h2>
                <p className="faculty-link"><strong>Name:</strong> {facultyData.name}</p>
                <p className="faculty-link"><strong>Email:</strong> {facultyData.email}</p>
                <p className="faculty-link"><strong>Phone:</strong> {facultyData.phoneNumber}</p>
                <p className="faculty-link"><strong>Department:</strong> {facultyData.department}</p>
            </div>

            {/* Research Publications Section */}
            <div>
                <h2>Research Publications</h2>
                {facultyData.publications.map((publication, index) => (
                    <p key={index} className="faculty-link">{publication.details}</p>
                ))}
                <button onClick={() => setShowPublicationModal(true)}>Add Publication</button>
            </div>

            {/* Modal for Adding Publications */}
            {showPublicationModal && (
                <div style={{ position: 'fixed', top: '20%', left: '30%', backgroundColor: 'white', padding: '20px', border: '1px solid black' }}>
                    <h3>Add Publication</h3>
                    <select value={newPublication.type} onChange={e => setNewPublication({ ...newPublication, type: e.target.value })}>
                        <option value="">Select Type</option>
                        <option value="journal">Journal Publication</option>
                        <option value="conference">Conference</option>
                        <option value="book">Book</option>
                        <option value="bookChapter">Book Chapter</option>
                        <option value="article">Article</option>
                    </select>
                    <br />
                    <textarea 
                        value={newPublication.details} 
                        onChange={e => setNewPublication({ ...newPublication, details: e.target.value })}
                        style={{ width: '100%', minHeight: '100px', marginTop: '10px' }}
                    ></textarea>
                    <br />
                    <button onClick={addPublication}>Add</button>
                    <button onClick={() => setShowPublicationModal(false)} style={{ marginLeft: '10px' }}>Cancel</button>
                </div>
            )}
        </div>
    );
};

export default FacultyProfile;
