import React, { useState, useEffect } from 'react';

function FacultyDirectory() {
    const [facultyMembers, setFacultyMembers] = useState([]);

    useEffect(() => {
        // Fetch faculty data from your API
        fetch('/api/faculty')
            .then(response => response.json())
            .then(data => setFacultyMembers(data))
            .catch(error => console.error('Error fetching faculty data:', error));
    }, []);

    // Function to sort faculty members alphabetically by name
    const sortedFacultyMembers = facultyMembers.sort((a, b) => a.name.localeCompare(b.name));

    return (
        <div className="faculty-directory">
            <h1>Faculty Directory</h1>
            {sortedFacultyMembers.map(member => (
                <div key={member.id} className="faculty-member">
                    <h3>{member.name}</h3>
                    <p>{member.designation}</p>
                    <p>{member.department}</p>
                    <p>Email: {member.email}</p>
                    <p>Phone: {member.phoneNumber}</p>
                </div>
            ))}
        </div>
    );
}

export default FacultyDirectory;
