import React, { useState, useEffect } from 'react';
import './CS.css';

const Faculty = () => {
  const [facultyMembers, setFacultyMembers] = useState([]);

  // Function to fetch faculty by department
  const fetchFacultyByDepartment = async (department) => {
    try {
      const response = await fetch(`http://localhost:3001/api/faculty/department/${department}`);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      setFacultyMembers(data);
    } catch (error) {
      console.error('Error fetching faculty:', error);
    }
  };

  // useEffect to call the fetch function when the component mounts
  useEffect(() => {
    fetchFacultyByDepartment("Computer Systems"); // Replace with the actual department name
  }, []);

return (
    <div className="faculty">
      <h3>Faculty Members</h3>
      <ul>
        {facultyMembers.length > 0 ? (
          facultyMembers.map(member => (
            <li key={member.id} className="faculty-list-item"> {/* Added class name here */}
              {/* ... */}
            </li>
          ))
        ) : (
          <p>No faculty members found in this department.</p>
        )}
      </ul>
      {/* ... */}
    </div>
);

};

export default Faculty;
