import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './GenericStyles.css'; // Ensure this path is correct

const FacultyProfile = () => {
    const [facultyData, setFacultyData] = useState({
        username: '', 
        name: '', 
        email: '', 
        phoneNumber: '', 
        department: ''
    });
    const { id } = useParams();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`http://localhost:3001/faculty/${id}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setFacultyData(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };

        fetchData();
    }, [id]);

    const handleUpdate = async (field) => {
        try {
            const response = await fetch(`http://localhost:3001/updateFaculty`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ ...facultyData, fieldToUpdate: field }),
            });

            if (!response.ok) throw new Error(await response.text());
            alert(`${field} updated successfully`);

            // Fetch updated data to reflect the changes
            const updatedResponse = await fetch(`http://localhost:3001/faculty/${id}`);
            const updatedData = await updatedResponse.json();
            setFacultyData(updatedData);
        } catch (error) {
            console.error('Update error:', error);
            alert('Update failed: ' + error.message);
        }
    };

    return (
        <div className="cs-container">
            <h1>Welcome {facultyData.username}</h1>
            <div className="form">
                {/* Form fields and buttons */}
                {/* ... */}
            </div>

            {/* Displaying updated data with faculty-link style */}
            <div className="profile-display">
                <h2>Profile Information</h2>
                <p className="faculty-link">{facultyData.name}</p>
                <p className="faculty-link">Email: {facultyData.email}</p>
                <p className="faculty-link">Phone: {facultyData.phoneNumber}</p>
                <p className="faculty-link">Department: {facultyData.department}</p>
            </div>
        </div>
    );
};

export default FacultyProfile;
