import React, { useState, useEffect } from 'react';
import './FacultyDirectory.css';

function FacultyDirectory() {
  const [facultyData, setFacultyData] = useState({});
  const [selectedDepartment, setSelectedDepartment] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001/api/faculty-directory')
      .then(response => response.json())
      .then(data => {
        const sortedData = data.sort((a, b) => a.name.localeCompare(b.name));
        const categorizedData = sortedData.reduce((acc, member) => {
          const initial = member.name.charAt(0).toUpperCase();
          if (!acc[initial]) {
            acc[initial] = [];
          }
          acc[initial].push(member);
          return acc;
        }, {});
        setFacultyData(categorizedData);
      })
      .catch(error => console.error('Error fetching faculty data:', error));
  }, []);

  const handleDepartmentChange = (event) => {
    setSelectedDepartment(event.target.value);
  };

  const clearFilter = () => {
    setSelectedDepartment('');
  };

  return (
    <div className="faculty-directory">
      <h1>Faculty Directory</h1>
      <div className="filter-section">
        <div className="department-filter">
          <label htmlFor="department-select">Departments:</label>
          <select id="department-select" onChange={handleDepartmentChange} value={selectedDepartment}>
            <option value="">Choose Department</option>
            {/* Map your departments here */}
          </select>
        </div>
        <button onClick={clearFilter} className="clear-button">Clear</button>
      </div>

      {Object.keys(facultyData).map(initial => (
        <div key={initial}>
          <h2>{initial}</h2>
          <div className="faculty-row">
            {facultyData[initial].map((member, index) => (
              <div key={member.id} className={`faculty-member ${index % 3 === 2 ? 'last-in-row' : ''}`}>
                <h3 className="faculty-name">{member.name}</h3>
                <p className="faculty-designation">{member.designation}</p>
                <p className="faculty-department">{member.department}</p>
                {member.email && (
                  <p><i className="fa fa-envelope"></i> {member.email}</p>
                )}
                {member.phoneNumber && (
                  <p><i className="fa fa-phone"></i> {member.phoneNumber}</p>
                )}
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default FacultyDirectory;
