import React, { useState } from 'react';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
import './App.css';

function CustomNavbar() {
  const [showDropdown, setShowDropdown] = useState({});

  const handleMouseEnter = (dropdown) => {
    setShowDropdown({ ...showDropdown, [dropdown]: true });
  };

  const handleMouseLeave = (dropdown) => {
    setShowDropdown({ ...showDropdown, [dropdown]: false });
  };
  
  return (
    <Navbar expand="lg" bg="light" variant="light">
    <Container>
      <Navbar.Brand href="#home">QUEST | Nawabshah</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="me-auto">
          <Nav.Link href="#home">Home</Nav.Link>
  
          {/* Academics Dropdown */}
          <NavDropdown 
  title="Academics" 
  id="nav-dropdown-academics"
  show={showDropdown.academics}
  onMouseEnter={() => handleMouseEnter('academics')}
  onMouseLeave={() => handleMouseLeave('academics')}>

  {/* Sub-dropdown for Programs */}
  <NavDropdown
    title="Programs"
    id="nav-dropdown-programs"
    drop="end"
    show={showDropdown.programs}
    onMouseEnter={() => handleMouseEnter('programs')}
    onMouseLeave={() => handleMouseLeave('programs')}>

    {/* Items under Programs */}
    <NavDropdown.Item href="#graduate">Graduate</NavDropdown.Item>
    <NavDropdown.Item href="#postgraduate">Postgraduate</NavDropdown.Item>

  </NavDropdown>

  {/* Other items for Academics dropdown */}
  {/* ... */}
  </NavDropdown>

  
          {/* Faculties Dropdown */}
          <NavDropdown 
            title="Faculties" 
            id="nav-dropdown-faculties"
            show={showDropdown.faculties}
            onMouseEnter={() => handleMouseEnter('faculties')}
            onMouseLeave={() => handleMouseLeave('faculties')}>
            {/* Dropdown items for Faculties */}
          </NavDropdown>
  
          
        </Nav>
      </Navbar.Collapse>
    </Container>
  </Navbar>
  

  );
}

export default CustomNavbar;
