import React, { useState } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import './App.css';
import FacultyPopup from './components/FacultyPopup'; // Import the FacultyPopup component

function CustomNavbar() {
  const [showPopup, setShowPopup] = useState(false);

   return (
    <Navbar expand="lg" bg="light" variant="light">
      <Container>
        <Navbar.Brand href="#home">QUEST | Nawabshah</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            <div className="nav-item" onMouseEnter={() => handleMouseEnter('academics')} onMouseLeave={() => handleMouseLeave('academics')}>
              <span className="nav-link">Academics</span>
              {showPopup.academics && <AcademicsPopup />}
            </div>
            {/* Repeat for other popups */}
            <div className="nav-item" onMouseEnter={() => handleMouseEnter('faculties')} onMouseLeave={() => handleMouseLeave('faculties')}>
              <span className="nav-link">Faculties</span>
              {showPopup.faculties && <FacultyPopup />}
            </div>
            {/* ... other popups like admissions, students, etc. */}
            {/* Add HostelsPopup with changed name */}
            <div className="nav-item" onMouseEnter={() => handleMouseEnter('hostels')} onMouseLeave={() => handleMouseLeave('hostels')}>
              <span className="nav-link">Hostels and Accommodations</span>
              {showPopup.hostels && <HostelsPopup />}
            </div>
            {/* ... other popups like news, about, etc. */}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default CustomNavbar;
