import React, { useState, useEffect } from 'react';
import './FacultyDirectory.css'; // Make sure to link your CSS file

function FacultyDirectory() {
  const [facultyData, setFacultyData] = useState({});

  useEffect(() => {
    fetch('http://localhost:3001/api/faculty-directory')
      .then(response => response.json())
      .then(data => {
        const sortedData = data.sort((a, b) => cleanName(a.name).localeCompare(cleanName(b.name)));
        const categorizedData = sortedData.reduce((acc, member) => {
          const initial = cleanName(member.name).charAt(0).toUpperCase();
          if (!acc[initial]) {
            acc[initial] = [];
          }
          acc[initial].push(member);
          return acc;
        }, {});
        setFacultyData(categorizedData);
      })
      .catch(error => console.error('Error fetching faculty data:', error));
  }, []);

  function cleanName(fullName) {
    return fullName.replace(/(Prof.\.|Dr\.|Engr\.)\s?/, '').split(' ')[0];
  }

  return (
    <div className="faculty-directory">
      <h1>Faculty Directory</h1>
      {Object.keys(facultyData).map(initial => (
        <div key={initial}>
          <h2>{initial}</h2>
          <div className="faculty-row">
            {facultyData[initial].map((member, index) => (
              <div key={member.id} className={`faculty-member ${index % 3 === 2 ? '' : 'member-separator'}`}>
                <h3 className="faculty-name">{member.name}</h3>
                <p className="faculty-designation">{member.designation}</p>
                <p className="faculty-department">{member.department}</p>
                {member.email && (
                  <p><i className="fa fa-envelope"></i> {member.email}</p>
                )}
                {member.phoneNumber && (
                  <p><i className="fa fa-phone"></i> {member.phoneNumber}</p>
                )}
                {member.officeHours && (
                  <p><i className="fa fa-clock"></i> Office hours: {member.officeHours}</p>
                )}
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default FacultyDirectory;
