// CustomNavbar.js
import React, { useState } from 'react';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
import './App.css';

function CustomNavbar() {
  const [showDropdown, setShowDropdown] = useState({});

  const handleMouseEnter = (dropdown) => {
    setShowDropdown({ ...showDropdown, [dropdown]: true });
  };

  const handleMouseLeave = (dropdown) => {
    setShowDropdown({ ...showDropdown, [dropdown]: false });
  };

  return (
    <Navbar expand="lg" bg="primary" variant="dark" style={{ backgroundColor: '#516BEB' }}>
      <Container>
        <Navbar.Brand href="#home">QUEST | Nawabshah</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            
            {/* Example of a mega menu dropdown */}
            <NavDropdown 
              title="Academics"
              id="nav-dropdown-academics"
              show={showDropdown.academics}
              onMouseEnter={() => handleMouseEnter('academics')}
              onMouseLeave={() => handleMouseLeave('academics')}
              className="mega-dropdown">
              <div className="dropdown-menu-content">
                {/* Your mega menu content goes here */}
                {/* Repeat this structure for other dropdown items */}
              </div>
            </NavDropdown>

            {/* Add other NavDropdowns as needed */}
          </Nav>
          <form className="d-flex">
            <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
            <button className="btn btn-outline-light" type="submit">Search</button>
          </form>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default CustomNavbar;
