import React, { useState, useEffect } from 'react';
import './FacultyDirectory.css'; // Assume this is the CSS file for styling

function FacultyDirectory() {
  const [facultyData, setFacultyData] = useState({});

  useEffect(() => {
    fetch('http://localhost:3001/api/faculty-directory')
      .then(response => response.json())
      .then(data => {
        const sortedData = data.sort((a, b) => a.name.localeCompare(b.name));
        const categorizedData = sortedData.reduce((acc, member) => {
          const initial = member.name.charAt(0).toUpperCase();
          if (!acc[initial]) {
            acc[initial] = [];
          }
          acc[initial].push(member);
          return acc;
        }, {});
        setFacultyData(categorizedData);
      })
      .catch(error => console.error('Error fetching faculty data:', error));
  }, []);

  return (
    <div className="faculty-directory">
      <h1>Faculty Directory</h1>
      {Object.keys(facultyData).map(initial => (
        <div key={initial}>
          <h2>{initial}</h2>
          <div className="faculty-row">
          {facultyData[initial].map(member => (
            <div key={member.id} className="faculty-member">
            <h3>{member.name}</h3>
            <p>{member.designation}</p>
            <p>{member.department}</p>
            {member.email && <p>Email: {member.email}</p>}
            {member.phoneNumber && <p>Phone: {member.phoneNumber}</p>}
        </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default FacultyDirectory;
