import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const FacultyProfileDetails = () => {
  const [facultyData, setFacultyData] = useState(null);
  const { username } = useParams(); // Assuming the URL parameter is 'username'

  useEffect(() => {
    const fetchFacultyDetails = async () => {
      try {
        const response = await fetch(`http://localhost:3001/api/faculty/${username}`);
        if (!response.ok) {
          throw new Error('Failed to fetch faculty details');
        }
        const data = await response.json();
        setFacultyData(data);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchFacultyDetails();
  }, [username]); // Depend on username to refetch when it changes

  if (!facultyData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{facultyData.name}</h2>
      <p>Designation: {facultyData.designation}</p>
      <p>Email: {facultyData.email}</p>
      <p>Phone: {facultyData.phoneNumber}</p>
      {/* Add more profile details here */}
    </div>
  );
};

export default FacultyProfileDetails;
