import React, { useState } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import FacultyPopup from './components/FacultyPopup'; // Ensure this path is correct


function CustomNavbar({ stickyClass, navbarOpacity }) {
  const [showPopup, setShowPopup] = useState(false);

  // Function to toggle popup
  const togglePopup = (status) => setShowPopup(status);

  return (
    <Navbar expand="lg" className={`w-100 ${stickyClass || 'position-absolute'}`} style={{ opacity: navbarOpacity }}>
      <Container>
        <Navbar.Toggle aria-controls='basic-navbar-nav' />
        <Navbar.Collapse id='basic-navbar-nav'>
          <Nav className='me-auto justify-content-center w-100'>
            <Nav.Link as={Link} to='/' className='text-uppercase'>Home</Nav.Link>
            <Nav.Link as={Link} to='/about' className='text-uppercase'>About us</Nav.Link>
            <Nav.Link as={Link} to='/blog' className='text-uppercase'>Blog</Nav.Link>
            <Nav.Link as={Link} to='/contact' className='text-uppercase'>Get in touch</Nav.Link>

            <div 
              className='nav-item'
              onMouseEnter={() => togglePopup(true)} 
              onMouseLeave={() => togglePopup(false)}>
              <span className='text-uppercase'>Faculties</span>
              {showPopup && <FacultyPopup />}
            </div>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default CustomNavbar;
