import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';

const FacultyProfile = () => {
    const [facultyData, setFacultyData] = useState({});
    const { id } = useParams();
    const location = useLocation();

    useEffect(() => {
        // Function to fetch faculty data
        const fetchData = async () => {
            try {
                const response = await fetch(`http://localhost:3001/faculty/${id}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setFacultyData(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };

        // If faculty data is passed in state, use it directly
        if (location.state) {
            setFacultyData(location.state);
        } else {
            // Fetch the faculty data if no state is passed
            fetchData();
        }
    }, [id, location.state]);
    
    return (
        <div>
            <h1>Faculty Profile</h1>
            <p>Username: {facultyData.username}</p>
            <p>Name: {facultyData.name}</p>
            {/* Add more fields as needed */}
        </div>
    );
};

export default FacultyProfile;
