import React, { useState, useEffect } from 'react';
import './FacultyDirectory.css';

function FacultyDirectory() {
  const [facultyData, setFacultyData] = useState({});
  const [selectedDepartment, setSelectedDepartment] = useState('Choose Department');

  useEffect(() => {
    fetch('http://localhost:3001/api/faculty-directory')
      .then(response => response.json())
      .then(data => {
        // ... Sorting and categorizing logic
      })
      .catch(error => console.error('Error fetching faculty data:', error));
  }, []);

  const handleDepartmentChange = (event) => {
    setSelectedDepartment(event.target.value);
    // ... Logic to filter faculty data based on selected department
  };

  return (
    <div className="faculty-directory">
      <h1>Faculty Directory</h1>
      <h3>Departments</h3>
      <select value={selectedDepartment} onChange={handleDepartmentChange} className="department-select">
        <option value="Choose Department">Choose Department</option>
        <option value="FoC&M">Faculty of Civil and Mechanical Engineering (FoC&M)</option>
        <option value="FEECE">Faculty of Electrical, Electronic & Computer Systems Engineering (FEECE)</option>
        <option value="FoS">Faculty of Science (FoS)</option>
        <option value="FoEE">Faculty of Environmental Engineering (FoEE)</option>
        <option value="FoT">Faculty of Technology (FoT)</option>
        {/* Add more options as needed */}
      </select>

      {/* Render faculty members here */}
    </div>
  );
}

export default FacultyDirectory;
