import React, { useState } from 'react';

const FacultyRegister = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [createdBy, setCreatedBy] = useState(''); // Assuming createdBy is a username or identifier
    // Add state for updatedBy if required
    const [updatedBy, setUpdatedBy] = useState('');

    const handleRegister = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:3001/register', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ 
                    username, 
                    password, 
                    name, 
                    createdBy,
                    updatedBy // Include updatedBy if needed
                })
            });

            if (!response.ok) throw new Error(await response.text());
            alert('User registered successfully');
        } catch (error) {
            console.error('Registration error:', error);
            alert('Registration failed: ' + error.message);
        }
    };

    return (
        <div>
            <form onSubmit={handleRegister}>
                <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} />
                <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
                <input type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)} />
                <input type="text" placeholder="Created By" value={createdBy} onChange={e => setCreatedBy(e.target.value)} />
                <input type="text" placeholder="Updated By" value={updatedBy} onChange={e => setUpdatedBy(e.target.value)} />
                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default FacultyRegister;
