import React, { useState, useEffect } from 'react';

function FacultyDirectory() {
  const [facultyMembers, setFacultyMembers] = useState([]);

  useEffect(() => {
    // Fetch the faculty data from your API or server
    fetch('/api/faculty-directory')
      .then(response => response.json())
      .then(data => setFacultyMembers(data.sort((a, b) => a.name.localeCompare(b.name))))
      .catch(error => console.error('Error fetching faculty data:', error));
  }, []);

  const groupByFirstLetter = (members) => {
    return members.reduce((acc, current) => {
      const letter = current.name.charAt(0).toUpperCase();
      if (!acc[letter]) {
        acc[letter] = [];
      }
      acc[letter].push(current);
      return acc;
    }, {});
  }

  const groupedFacultyMembers = groupByFirstLetter(facultyMembers);

  return (
    <div className="faculty-directory">
      <h1>Faculty Directory</h1>
      {Object.keys(groupedFacultyMembers).map((letter) => (
        <div key={letter}>
          <h2>{letter}</h2>
          <hr />
          {groupedFacultyMembers[letter].map(member => (
            <div key={member.id} className="faculty-member">
              {/* Faculty member details */}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}

export default FacultyDirectory;
