import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './GenericStyles.css';

const FacultyProfile = () => {
    const [facultyData, setFacultyData] = useState({
        username: '',
        name: '',
        email: '',
        phoneNumber: '',
        department: '',
        publications: []
    });
const [showPublicationModal, setShowPublicationModal] = useState(false);
    const [showModifyModal, setShowModifyModal] = useState(false);
    const [newPublication, setNewPublication] = useState({ type: '', details: '' });
    const [modifyingPublication, setModifyingPublication] = useState({ id: null, details: '' });
    const [tempValues, setTempValues] = useState({ name: '', email: '', phoneNumber: '', department: '' });
    const { id } = useParams();
    const [departments, setDepartments] = useState([]);
    
    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch faculty data
                const facultyResponse = await fetch(`http://localhost:3001/faculty/${id}`);
                if (!facultyResponse.ok) {
                    throw new Error(`HTTP error! status: ${facultyResponse.status}`);
                }
                const facultyData = await facultyResponse.json();
                setFacultyData(facultyData);
                setTempValues({
                    name: facultyData.name,
                    email: facultyData.email,
                    phoneNumber: facultyData.phoneNumber,
                    department: facultyData.department_id
                });

                // Fetch departments data
                const departmentsResponse = await fetch('http://localhost:3001/api/departments');
                if (!departmentsResponse.ok) {
                    throw new Error(`HTTP error! status: ${departmentsResponse.status}`);
                }
                const deptData = await departmentsResponse.json();
                setDepartments(deptData);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, [id]);

    const handleUpdate = async (field, value) => {
        try {
            const updatedData = { ...facultyData, [field]: value };
            const response = await fetch(`http://localhost:3001/updateFaculty`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(updatedData)
            });

            if (!response.ok) throw new Error(await response.text());
            setFacultyData(updatedData);
            alert(`${field} updated successfully`);
        } catch (error) {
            console.error('Update error:', error);
            alert('Update failed: ' + error.message);
        }
    };

    return (
        <div className="cs-container">
            <h1>Welcome {facultyData.username}</h1>

            {/* Faculty Details Update Section */}
            <div className="form">
                {/* Name */}
                <div>
                    <label>Name:</label>
                    <input
                        type="text"
                        value={tempValues.name}
                        onChange={e => setTempValues({ ...tempValues, name: e.target.value })}
                    />
                    <button onClick={() => handleUpdate('name', tempValues.name)}>Update Name</button>
                </div>

                {/* Email */}
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={tempValues.email}
                        onChange={e => setTempValues({ ...tempValues, email: e.target.value })}
                    />
                    <button onClick={() => handleUpdate('email', tempValues.email)}>Update Email</button>
                </div>

                {/* Phone Number */}
                <div>
                    <label>Phone Number:</label>
                    <input
                        type="text"
                        value={tempValues.phoneNumber}
                        onChange={e => setTempValues({ ...tempValues, phoneNumber: e.target.value })}
                    />
                    <button onClick={() => handleUpdate('phoneNumber', tempValues.phoneNumber)}>Update Phone Number</button>
                </div>

                {/* Department */}
                <div>
                    <label>Department:</label>
                    <select
                        value={tempValues.department}
                        onChange={e => setTempValues({ ...tempValues, department: e.target.value })}
                    >
                        {departments.map(dept => (
                            <option key={dept.id} value={dept.id}>{dept.name}</option>
                        ))}
                    </select>
                    <button onClick={() => handleUpdate('department', tempValues.department)}>Update Department</button>
                </div>
            </div>

            {/* Displaying Updated Faculty Data */}
            <div className="profile-display">
                <h2>Profile Information</h2>
                <p><strong>Name:</strong> {facultyData.name}</p>
                <p><strong>Email:</strong> {facultyData.email}</p>
                <p><strong>Phone:</strong> {facultyData.phoneNumber}</p>
                <p><strong>Department:</strong> {facultyData.department}</p>
            </div>
        </div>
    );
};

export default FacultyProfile;
