import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const FacultyProfileDetails = () => {
    const [facultyData, setFacultyData] = useState(null);
    const [error, setError] = useState(null);
    const { facultyUsername } = useParams(); // Make sure this matches the route parameter

    useEffect(() => {
        fetch(`http://localhost:3001/api/faculty/${facultyUsername}`) // Ensure this endpoint matches your server route
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => setFacultyData(data))
            .catch(error => {
                console.error('Fetch error:', error);
                setError(error.toString());
            });
    }, [facultyUsername]);

    if (error) {
        return <div>Error: {error}</div>;
    }

    if (!facultyData) {
        return <div>Loading...</div>;
    }

    // Render faculty data
    return (
      <div>
      <h2>{facultyData.name}</h2>
      <p>Email: {facultyData.email}</p>
      <p>Phone Number: {facultyData.phoneNumber}</p>
      <p>Department: {facultyData.department}</p>
      <p>Designation: {facultyData.designation}</p>
      <p>Qualification: {facultyData.qualification}</p>
      <p>Objectives: {facultyData.objectives}</p>
      {/* Render other faculty details here */}
      <p>Current Portfolio: {facultyData.currentPortfolio}</p>
      <p>Diplomas: {facultyData.diplomas}</p>
      {/* Add more details as needed */}
      {/* Repeat for each attribute */}
  </div>
);
};

export default FacultyProfileDetails;
