import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const FacultyProfileDetails = () => {
    const [faculty, setFaculty] = useState(null);
    const { username } = useParams();

    useEffect(() => {
        fetch(`http://localhost:3001/api/faculty/${username}`)
            .then(response => response.json())
            .then(data => setFaculty(data))
            .catch(error => console.error('Error fetching faculty details:', error));
    }, [username]); // Ensure username is in dependency array

    if (!faculty) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>{faculty.name}</h2>
            <p>Designation: {faculty.designation}</p>
            <p>Email: {faculty.email}</p>
            <p>Phone: {faculty.phoneNumber}</p>
            {/* Add more profile details here */}
        </div>
    );
};

export default FacultyProfileDetails;
