import React, { useState, useEffect } from 'react';
import './CS.css';

const Faculty = () => {
  const [facultyMembers, setFacultyMembers] = useState([]);

  useEffect(() => {
    fetchFacultyByDepartment("Computer Systems"); // Replace with the correct department name
  }, []);

  const fetchFacultyByDepartment = async (department) => {
    try {
      const response = await fetch(`http://localhost:3001/api/faculty/department/${department}`);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      setFacultyMembers(data);
    } catch (error) {
      console.error('Error fetching faculty:', error);
    }
  };

  return (
    <div className="faculty">
      <h3>Faculty Members</h3>
      <ul>
        {facultyMembers.map(member => (
          <li key={member.id}>
            <a href={`/faculty/${member.username}`} className="faculty-link">
              <strong>{member.name}</strong> - {member.designation}
            </a>
            <br />
            Email: {member.email}<br />
            Phone: {member.phoneNumber}<br />
            Qualifications: {member.qualification}
          </li>
        ))}
      </ul>
      <p>
        For more information on faculty members, <a href="/faculty">visit the faculty page</a>.
      </p>
    </div>
  );
};

export default Faculty;
