import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Container, Nav, NavDropdown } from 'react-bootstrap';

// Import pages and components
import Home from './pages/Home/Home';
import Faculties from './pages/Faculties/Faculties';
import About from './pages/About/About';
import Blog from './pages/Blog/Blog';
import Contact from './pages/Contact/Contact';
import CS from './pages/Academics/CS/CS';
import FacultyLogin from './components/FacultyLogin';
import FacultyRegister from './components/FacultyRegister';
import FacultyProfileDetails from './components/FacultyProfileDetails';




function App() {
  const [stickyClass, setStickyClass] = useState('');
  const [navbarOpacity, setNavbarOpacity] = useState(1);
  const [departments, setDepartments] = useState([]); // State for departments

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    fetchDepartments(); // Fetch departments on component mount 

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [id]);

  const blogs = getBlogs();
  const handleScroll = () => {
    let windowHeight = window.scrollY;
    setStickyClass(windowHeight > 80 ? 'fixed-top' : '');

    // Adjust the opacity based on scroll position
    const newOpacity = windowHeight > 80 ? 0.7 : 1; // 0.7 is the transparency level when scrolled
    setNavbarOpacity(newOpacity);
};


// Function to fetch departments data from your API
const fetchDepartments = async () => {
  try {
    const response = await fetch('http://localhost:3001/api/departments');
    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
    const data = await response.json();
    setDepartments(data);
  } catch (error) {
    console.error('Error fetching departments:', error);
  }
};

  return (
    <Router>
