              <Nav.Link href='/' className='text-uppercase'>Home</Nav.Link>

            {/* Dynamic departments dropdown */}
              <NavDropdown title="Academics" id="academics-dropdown" className='text-uppercase'>
                {departments.map(dept => (
                <NavDropdown.Item key={dept.id} href={`/${dept.name.toLowerCase().replace(/\s+/g, '-')}`}>
                  {dept.name}
                </NavDropdown.Item>
              ))}
              </NavDropdown>

              <Nav.Link href='/about' className='text-uppercase'>About us</Nav.Link>
              <Nav.Link href='/blog' className='text-uppercase'>Blog</Nav.Link>
              <Nav.Link href='/contact' className='text-uppercase'>Get in touch</Nav.Link>
